import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import dayjs from 'dayjs';
import _ from 'lodash';

import { ChatArea } from '../components/ChatArea';
import { Navbar } from '../components/Navbar';
import { Sidebar } from '../components/Sidebar';
import { sendMessage, getMessages } from '../store/messages.slice';
import { sendThisUserIsTyping, sendThisUserStoppedTyping } from '../store/users.slice';
import { logout } from '../store/auth.slice';
import { RootState, Message } from '../utilities/types';
import { getUsers } from '../store/users.slice';

export const Main: React.FC = () => {
  const dispatch = useDispatch();
  const [messageInput, setMessageInput] = useState('');

  const { myUser } = useSelector((state: RootState) => state.authState);
  const { users, loading: usersLoading, onlineUsers, typingUsers } = useSelector((state: RootState) => state.usersState);
  const { messages, loading: messageLoading } = useSelector((state: RootState) => state.messagesState);

  const handleLogoutClick = (event: any) => {
    // 로그아웃시 localStorage 유저 정보 삭제
    localStorage.removeItem('user');

    dispatch(logout());
  };

  const debouncedTypingIndicationEmit = useCallback(
    _.debounce(() => dispatch(sendThisUserIsTyping(myUser!.username)), 500),
    [] // will be created only once initially
  );

  const handleSubmitForm = (event: any) => {
    event.preventDefault();

    if (messageInput && messageInput.trim() !== '') {
      const message: Message = {
        content: messageInput.trim(),
        date: dayjs().format(),
        author: myUser!.username,
      };

      dispatch(sendThisUserStoppedTyping(myUser!.username));
      dispatch(sendMessage(message));
    }

    setMessageInput('');
  };

  useEffect(() => {
    if (messageInput === '') {
      dispatch(sendThisUserStoppedTyping(myUser!.username));

      //Handles the last debounced emit
      setTimeout(() => {
        dispatch(sendThisUserStoppedTyping(myUser!.username));
      }, 500);
    }
  }, [messageInput, myUser, dispatch]);

  const handleChangeInput = (event: any) => {
    if (event.target.value !== '') debouncedTypingIndicationEmit();

    setMessageInput(event.target.value);
  };

  useEffect(() => {
    dispatch(getUsers());
    dispatch(getMessages());
  }, [dispatch]);

  const usersData = useMemo(() => {
    if (users.length < 1) {
      return [];
    }

    return users
      .map((user) => ({
        ...user,
        online: onlineUsers.some((onlineUsername) => onlineUsername === user.username),
      }))
      .sort((a, b) => a.username.localeCompare(b.username));
  }, [users, onlineUsers]);

  const reversedMessages = useMemo(() => {
    if (messages.length < 1) {
      return [];
    }

    return [...messages].reverse();
  }, [messages]);

  if (usersLoading || messageLoading) {
    return <div className='flex justify-center items-center h-screen bg-gray-100'>로딩중...</div>;
  }
  return (
    <>
      <Navbar onClick={handleLogoutClick} />
      <div className='flex m-0 content'>
        <Sidebar users={usersData} myUser={myUser} typingUsers={typingUsers} />
        <ChatArea
          messages={reversedMessages}
          messageInput={messageInput}
          handleSubmitForm={handleSubmitForm}
          handleChangeInput={handleChangeInput}
        />
      </div>
    </>
  );
};

export default Main;
